CREATE TABLE CUSTOMERS (ID INT NOT NULL AUTO_INCREMENT, NAME VARCHAR(255), PASSWORD VARCHAR(255), STREET VARCHAR(255), HOUSE VARCHAR(255), ZIP VARCHAR(255), PLACE VARCHAR(255), EMAIL VARCHAR(255), PHONE BIGINT, PRIMARY KEY(ID));
CREATE TABLE CARS (ID INT NOT NULL AUTO_INCREMENT, MAKE VARCHAR(255), MODEL VARCHAR(255), VERSION VARCHAR(255), DOORS INT, EMISSION DOUBLE, GROSS DOUBLE, NETT DOUBLE, PRIMARY KEY(ID));